'use strict';

// MODULES //

var addon = require( './../src/addon.node' );


// MAIN //

/**
* Evaluates the cumulative distribution function (CDF) for an studentized range with range `r`, Degree of freedom `v` at a value `x`.
*
* @private
* @param {number} x - input value
* @param {number} r - range
* @param {number} v - degree of freedom
* @returns {Probability} evaluated CDF
*
* @example
* var y = cdf( 0.5, 3.0, 2.0);
* // returns ~0.0644
*
* @example
* var y = cdf( 12.1, 17.0, 2.0);
* // returns ~0.913
*
* @example
* var y = cdf(0.5, 3.0, 2.0, 2);
* // returns ~0.01
*
* @example
* var y = cdf( NaN, 2.0, 2.0);
* // returns NaN
*
* @example
* var y = cdf( 2.0, -1.0, 3.0 );
* // returns NaN
*/
function cdf( x, r, v ) {
	return addon( x, r, v );
}


// EXPORTS //

module.exports = cdf;
